import React, {useState} from 'react';
import { Table, Tag, Space } from 'antd';
import { Button } from 'react-bootstrap';



const data = [
  {
    key: '1',
    name: 'John Brown',
    age: 32,
    address: 'New York No. 1 Lake Park',
    tags: ['nice', 'developer'],
  },
  {
    key: '2',
    name: 'Jim Green',
    age: 42,
    address: 'London No. 1 Lake Park',
    tags: ['loser'],
  },
  {
    key: '3',
    name: 'Joe Black',
    age: 32,
    address: 'Sidney No. 1 Lake Park',
    tags: ['cool', 'teacher'],
  },
];
const language = [
    { "ተ.ቁ": "", "ምርጫው የተካሄደበት ክልል/ከተማ መስተዳድር": "", "የተመዘገቡ መራጮች ቁጥር": "", "ከተመዘገቡ መራጮች መካከል ድምፅ የሰጡ በመቶኛ": "", "ክልሉ ያለው የተወካዮች ምክር ቤት መቀመጫ ብዛት": "", "ክልሉ ያለው የክልል ምክር ቤት መቀመጫ ብዛት": "", "መስከረም 20 ቀን ምርጫ የተከናወነበት የተወካዮች ምክር ቤት ምርጫ ክልል ብዛት": "", "ለተወካዮች ምክር ቤት መቀመጫ ያገኙ ፓርቲዎች እና የመቀመጫ ቁጥር": "", "ድጋሚ ቆጠራ የሚከናወንበት ምርጫ ክልል ብዛት": "", "ድጋሚ ምርጫ የሚከናወንበት ምርጫ ክልል ብዛት": "", "መስከረም 20 ቀን ምርጫ የተከናወነበት የክልል ምክር ቤት ምርጫ ክልል ብዛት": "", "ለክልል ምክር ቤት መቀመጫ ያገኙ ፓርቲዎች እና የመቀመጫ ቁጥር": "", "ድጋሚ ቆጠራ የሚከናወንበት ምርጫ ክልል ብዛት": "", "ድጋሚ ምርጫ የሚከናወንበት ምርጫ ክልል ብዛት": "", "በሁለቱም ምርጫ አይነቶች ምርመራ ላይ ያሉ ምርጫ ክልሎች ብዛት": "" },
  ]

export default function ResultTable() {
    const [language, setLanguage] = useState(false)
    const columns = [
        {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>{language ? 'ተ.ቁ' : 'no'}</p>,
          dataIndex: 'id',
          key: 'id',
          // render: text => <a>{text}</a>,
        },
        {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>{language ? 'ምርጫው የተካሄደበት ክልል/ከተማ መስተዳድር' : 'Region'}</p>,
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>{language ? 'የተመዘገቡ መራጮች ቁጥር' : 'Number of Registered Voters'}</p>,
          dataIndex: 'address',
          key: 'address',
        },

        {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>{language ? 'ከተመዘገቡ መራጮች መካከል ድምፅ የሰጡ በመቶኛ' : 'Number of Voted Voters from Registerd Voters in %'}</p>,
          key: 'tags',
          dataIndex: 'tags',
          render: tags => (
            <>
              {tags.map(tag => {
                let color = tag.length > 5 ? 'geekblue' : 'green';
                if (tag === 'loser') {
                  color = 'volcano';
                }
                return (
                  <Tag color={color} key={tag}>
                    {tag.toUpperCase()}
                  </Tag>
                );
              })}
            </>
          ),
        },  
        {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>{language ? 'ክልሉ ያለው የተወካዮች ምክር ቤት መቀመጫ ብዛት': 'Number of HOPR seats in The Region'}</p>,
          dataIndex: 'address',
          key: 'address',
        },
        {
            title: <p style={{fontSize: 12, color: '#1dc4e9'}}>{language ? 'ክልሉ ያለው የክልል ምክር ቤት መቀመጫ ብዛት' : 'Number of seats in the Regional Counsel'}</p>, 
            dataIndex: 'address',
            key: 'address'
        },
        {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>መስከረም 20 ቀን ምርጫ የተከናወነበት የተወካዮች ምክር ቤት ምርጫ ክልል ብዛት</p>, 
          dataIndex: 'address',
          key: 'address'
      },
      {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>ለተወካዮች ምክር ቤት መቀመጫ ያገኙ ፓርቲዎች እና የመቀመጫ ቁጥር</p>, 
          dataIndex: 'address',
          key: 'address'
      },
      {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>ድጋሚ ቆጠራ የሚከናወንበት ምርጫ ክልል ብዛት</p>,
          dataIndex: 'address',
          key: 'address'
      },
      {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>ድጋሚ ምርጫ የሚከናወንበት ምርጫ ክልል ብዛት</p>,
          dataIndex: 'address',
          key: 'address'
      },
      {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>መስከረም 20 ቀን ምርጫ የተከናወነበት የክልል ምክር ቤት ምርጫ ክልል ብዛት</p>,
          dataIndex: 'address',
          key: 'address'
      },
      {
          title: <p style={{fontSize: 12, color: '#1dc4e9'}}>በሁለቱም ምርጫ አይነቶች ምርመራ ላይ ያሉ ምርጫ ክልሎች ብዛት</p>,
          dataIndex: 'address',
          key: 'address'
      },
      ];
  return (
  <div style={{marginTop: '5%'}}>
      <Button  onClick={() => setLanguage(!language)}>{language ? 'English': 'Amharic'}</Button>
      <Table size='larg' columns={columns} dataSource={data} />
  </div>
  );
}
