import React, { useState, useEffect } from 'react';

import friend from './friends';
import Friend from './Friend';
import Chat from './Chat';

const Friends = ({ listOpen }) => {
    const [chatOpen, setChatOpen] = useState(listOpen);
    const [user, setUser] = useState([]);
    const [users, setUsers] = useState([]);

    const requests = async () =>{
        try{
            const response = await fetch('http://localhost:8000/data/requests/request_list_aproved/')
            const res = await response.json()
            console.log('data', res.results)
            setUsers(res.results)

        } catch(e){
            console.log(e)
        }
    }

    useEffect(() => {
        requests()
        setChatOpen(false);
    }, [listOpen]);

    const friendList = users.map((f) => {
        return (
            <Friend
                key={f.id}
                data={f}
                activeId={users.id}
                clicked={() => {
                    setChatOpen(true);
                    setUser(f);
                }}
            />
        );
    });

    return (
        <React.Fragment>
            {friendList}
            <Chat
                user={user}
                chatOpen={chatOpen}
                listOpen={listOpen}
                closed={() => {
                    setChatOpen(false);
                    setUser([]);
                }}
            />
        </React.Fragment>
    );
};

export default Friends;
