import React, { useState, useEffect } from 'react';

import friend from './friends';
import Friend from './Friend';
import Chat from './Chat';
import { useSelector } from 'react-redux';


const Friends = ({ listOpen }) => {
    const [chatOpen, setChatOpen] = useState(listOpen);
    const [user, setUser] = useState([]);
    const [users, setUsers] = useState([]);
    const account = useSelector((state) => state.account);
    const requests = async () =>{
        try{
            const response = await fetch('http://localhost:8000/data/requests/request_list_aproved/')
            const res = await response.json()
            console.log('data', res.results)

            setUsers(res.results)

        } catch(e){
            console.log(e)
        }
    }

    useEffect(() => {
        requests()
        setChatOpen(false);

        console.log(account)
    }, [listOpen]);

    return (
        <React.Fragment>
            <div style={{display: 'flex', flexDirection: 'column'}}>
            {users.map((item, index) =>(
               <>
               {item.status ?  
               <a style={{padding: 10, margin: 10, backgroundColor: '#385238fa', borderRadius: 10, color: 'white'}}><p style={{color: '#03fb00', paddingRight: 10, textDecoration: 'uppercase'}}>{item.owner}</p>{item.request_description}<p style={{fontSize: 12, color: 'gray'}}>{item.created_date}</p></a> 
               :  
               <a style={{padding: 10,margin: 10, backgroundColor: '#8f4444fa', color: '#c7fb00'}}><p style={{color: '#03fb00', paddingRight: 10, textDecoration: 'capitalize'}}>{item.owner}</p>{item.id}<p style={{fontSize: 12, color: 'gray'}}>{item.created_date}</p></a>}</>
            ))}
            </div>
        </React.Fragment>
    );
};

export default Friends;
