import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { Collapse } from 'antd';

const { Panel } = Collapse;


const Friends = ({ listOpen }) => {
    const [chatOpen, setChatOpen] = useState(listOpen);
    const [user, setUser] = useState([]);
    const [users, setUsers] = useState([]);
    const [notApproved, setNotApproved] = useState([])
    const account = useSelector((state) => state.account);
    const requests = async () =>{
        try{
            const response = await fetch('http://localhost:8000/data/requests/request_list_aproved/')
            const res = await response.json()
            console.log('data', res.results)

            setUsers(res.results)

        } catch(e){
            console.log(e)
        }
    }

    const requestsNotApproved = async () =>{
        try{
            const response = await fetch('http://localhost:8000/data/requests/request_list_notapproved/')
            const res = await response.json()
            setNotApproved(res.results)

        } catch(e){
            console.log(e)
        }
    }
    function callback(key) {
        console.log(key);
      }
    const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;

    useEffect(() => {
        requests()
        setChatOpen(false);
        requestsNotApproved()

        console.log(account)
    }, [listOpen]);

    return (
        <React.Fragment>
            <>
            {account.user.is_staff && !account.user.is_superuser ? 
            <div style={{display: 'flex', flexDirection: 'column'}}>
                {notApproved.map((item, index)=>(
                    <div style={{padding: 10,margin: 10, backgroundColor: '#8f4444fa', color: 'white'}}><p style={{color: 'white', paddingRight: 10, textDecoration: 'capitalize'}}>{item.owner}</p>{item.request_description}<p style={{fontSize: 12, color: 'aqua'}}>{item.created_date}</p>{account.user.is_superuser ? <div style={{display: 'flex',flexDirection: 'row',  justifyContent: 'end', alignItems: 'flex-end', width: '100%'}}><button style={{backgroundColor: 'turquoise', borderWidth: 0, marginRight: 10}}>Detail</button><button style={{backgroundColor: 'green', borderWidth: 0}}>Approve</button></div> : null}
                        <Collapse defaultActiveKey={['1']} onChange={callback}>
                        <Panel header="Detail" key="1">
                        <p>{text}</p>
                        </Panel>
                    </Collapse>
                    </div>
                 ))}
            {users.map((item, index) =>(
               <div style={{padding: 10, margin: 10, backgroundColor: '#385238fa', borderRadius: 10, color: 'white'}}><p style={{color: '#03fb00', paddingRight: 10, textDecoration: 'uppercase'}}>{item.owner}</p>{item.request_description}<p style={{fontSize: 12, color: 'gray'}}>{item.created_date}</p>
                     <Collapse defaultActiveKey={['1']} onChange={callback}>
                        <Panel header="Detail" key="1">
                        <p>{text}</p>
                        </Panel>
                    </Collapse>
               </div> 
               ))}
            </div> : null}
            </>
            <>
            {account.user.is_superuser ? 
             <div style={{display: 'flex', flexDirection: 'column'}}>
                 {notApproved.map((item, index)=>(
                    <div style={{padding: 10,margin: 10, backgroundColor: '#8f4444fa', color: 'white'}}><p style={{color: 'white', paddingRight: 10, textDecoration: 'capitalize'}}>{item.owner}</p>{item.request_description}<p style={{fontSize: 12, color: 'aqua'}}>{item.created_date}</p>{account.user.is_superuser ? <div style={{display: 'flex',flexDirection: 'row',  justifyContent: 'end', alignItems: 'flex-end', width: '100%'}}><button style={{backgroundColor: 'green', borderWidth: 0}}>Approve</button></div> : null}
                        <Collapse defaultActiveKey={['1']} onChange={callback}>
                        <Panel header="Detail" key="1">
                        <p>{text}</p>
                        </Panel>
                    </Collapse>
                    </div>
                 ))}
             {users.map((item, index) =>(
                <a style={{padding: 10, margin: 10, backgroundColor: '#385238fa', borderRadius: 10, color: 'white'}}><p style={{color: '#03fb00', paddingRight: 10, textDecoration: 'uppercase'}}>{item.owner}</p>{item.request_description}<p style={{fontSize: 12, color: 'gray'}}>{item.created_date}</p></a> 
             ))}
             </div> : null}
            </>
        </React.Fragment>
    );
};

export default Friends;
